void claw_test_thread (void) {
	for (;;) {
		task_sleep (TIME_200MS);//this was set to run faster but seemed to cause problems
		claw_test_draw ();
	}
}



//up test button pressed
void claw_test_up (void) {
	if (claw_test_command < LAST_TEST)
		claw_test_command++;
}



//down test button pressed
void claw_test_down (void) {
	if (claw_test_command > FIRST_TEST)
		claw_test_command--;
}



//escape test button pressed
void claw_test_escape (void) {
	// stop everything
	claw_stop();
	window_pop();
}



//enter test button pressed
void claw_test_enter (void) {
	sound_send (SND_TEST_ENTER);
  	switch (claw_test_command) {
  		case BUMPLEFT:
			claw_bump_left();
			claw_test_command = BUMPLEFT;
  		break;
  		case BUMPRIGHT:
			claw_bump_right();
			claw_test_command = BUMPRIGHT;
  		break;
  		case LEFT:
			claw_go_left();
			claw_test_command = STOP;
  		break;
  		case RIGHT:
			claw_go_right();
			claw_test_command = STOP;
  		break;
  		case STOP:
			claw_stop();
			claw_test_command = BUMPLEFT;
  		break;
  	}
}



