void elevator_test_thread (void) {
	for (;;) {
		task_sleep (TIME_200MS);//this was set to run faster but seemed to cause problems
		elevator_test_draw ();
	}
}


//up test button pressed
void elevator_test_up (void) {
	if (elevator_test_command < LAST_TEST)
		elevator_test_command++;
}



//down test button pressed
void elevator_test_down (void) {
	if (elevator_test_command > FIRST_TEST)
		elevator_test_command--;
}


//escape test button pressed
void elevator_test_escape (void) {
	// stop everything
	elevator_stop();
	window_pop();
}


//enter test button pressed
void elevator_test_enter (void) {
	sound_send (SND_TEST_ENTER);
  	switch (elevator_test_command) {
  		case BUMP_UP:
			elevator_bump_up();
			elevator_test_command = BUMP_UP;
  		break;
  		case BUMP_DOWN:
			elevator_bump_down();
			elevator_test_command = BUMP_DOWN;
  		break;
  		case UP:
			elevator_move_up();
			elevator_test_command = DOWN;
  		break;
  		case DOWN:
			elevator_move_down();
			elevator_test_command = STOP;
  		break;
  		case STOP:
			elevator_stop();
			elevator_test_command = BUMP_UP;
  		break;
  	}
}



