	dmd_alloc_pair_clean ();
	dmd_sched_transition (&trans_scroll_down);
	huxley_animation_display_effect (IMG_HUXLEY_B1_START, IMG_HUXLEY_B1_END);
	task_sleep_sec (1);
			dmd_clean_page_low ();
