void huxley_hit_effect_deff(void) {
	U8 			huxley_MessageCounter;
	huxley_MessageCounter = random_scaled(5);
	dmd_clean_page_high ();//
	dmd_clean_page_low ();//

	sound_send (EXPLOSION1_SHORT);
	task_sleep (TIME_100MS);
	sound_send (EXPLOSION1_SHORT);
	task_sleep (TIME_100MS);
	sound_send (EXPLOSION1_SHORT);
	task_sleep (TIME_100MS);
	sound_send (EXPLOSION1_MED);
	task_sleep (TIME_100MS);
	sound_send (EXPLOSION1_LONG);
	task_sleep (TIME_100MS);

	play_huxley_sounds();

	switch (huxley_MessageCounter) {
		default:
		case 0:
			huxley_animation_display_effect (IMG_HUXLEY_A_START, IMG_HUXLEY_A_END);
			huxley_frame_with_words_display_fipps_effect (IMG_HUXLEY_A_END, DMD_MIDDLE_X - 30, DMD_BIG_CY_Bot, "HUXLEY");
			break;
		case 1:
			huxley_animation_display_effect (IMG_HUXLEY_B1_START, IMG_HUXLEY_B1_END);
			huxley_frame_with_words_display_fipps_effect (IMG_HUXLEY_B1_END, DMD_MIDDLE_X - 30, DMD_BIG_CY_Bot, "HUXLEY");
			break;
		case 2:
			huxley_animation_display_effect (IMG_HUXLEY_B2_START, IMG_HUXLEY_B2_END);
			huxley_frame_with_words_display_fipps_effect (IMG_HUXLEY_B2_END, DMD_MIDDLE_X - 30, DMD_BIG_CY_Bot, "HUXLEY");
			break;
		case 3:
			huxley_animation_display_effect (IMG_HUXLEY_D_START, IMG_HUXLEY_D_END);
			huxley_frame_with_words_display_fipps_effect (IMG_HUXLEY_D_END, DMD_MIDDLE_X + 30, DMD_BIG_CY_Bot, "HUXLEY");
			break;
		case 4:
			huxley_animation_display_effect (IMG_HUXLEY_E_START, IMG_HUXLEY_E_END);
			huxley_frame_with_words_display_fipps_effect (IMG_HUXLEY_E_END, DMD_MIDDLE_X - 30, DMD_BIG_CY_Bot, "HUXLEY");
			break;
		}//end of switch
	task_sleep_sec (1);
	deff_exit ();
}//end of mode_effect_deff



void huxley_effect_deff(void) {
	U8 frame = 0;
	U8 frame_counter = 0;
	for (;;) {
		//change the background every 5 seconds or so
		if (++frame_counter % 10 == 0) { if (frame >= 11) frame = 0; else frame ++; 	play_huxley_sounds(); }
		dmd_alloc_pair_clean();
		dmd_map_overlay ();
		dmd_clean_page_low ();
		dmd_draw_thin_border (dmd_low_buffer);
		sprintf ("%d", huxley_mode_timer);
		font_render_string_center (&font_var5, DMD_MIDDLE_X - 50, DMD_SMALL_CY_5, sprintf_buffer);
		font_render_string_center (&font_var5, DMD_MIDDLE_X + 50, DMD_SMALL_CY_5, sprintf_buffer);
		sprintf_score (huxley_mode_score);
		font_render_string_center (&font_var5, DMD_MIDDLE_X, DMD_SMALL_CY_5, sprintf_buffer);
		switch (frame) {
			case 0:	font_render_string_center (&font_fipps, DMD_MIDDLE_X + 35, DMD_BIG_CY_Top, "HUXLEY"); break; //ok
			case 2:	font_render_string_center (&font_fipps, DMD_MIDDLE_X + 35, DMD_BIG_CY_Bot, "HUXLEY"); break; //ok
			case 4:	font_render_string_center (&font_fipps, DMD_MIDDLE_X + 35, DMD_BIG_CY_Bot, "HUXLEY"); break; //ok
			case 6:	font_render_string_center (&font_fipps, DMD_MIDDLE_X + 35, DMD_BIG_CY_Bot, "HUXLEY"); break; //ok
			case 8:	font_render_string_center (&font_fipps, DMD_MIDDLE_X + 35, DMD_BIG_CY_Top, "HUXLEY"); break; //ok
			case 10: font_render_string_center (&font_fipps, DMD_MIDDLE_X - 35, DMD_BIG_CY_Top, "HUXLEY"); break; //ok
		}//end of switch
		dmd_text_outline ();
		dmd_alloc_pair ();
		switch (frame) {
			default:
			case 0:		frame_draw(IMG_HUXLEY_END_SCENE_START); break;
			case 1:		frame_draw(IMG_HUXLEY_END_SCENE_1); 	break;
			case 2:		frame_draw(IMG_HUXLEY_END_SCENE_2); 	break;
			case 3:		frame_draw(IMG_HUXLEY_END_SCENE_3); 	break;
			case 4:		frame_draw(IMG_HUXLEY_END_SCENE_4); 	break;
			case 5:		frame_draw(IMG_HUXLEY_END_SCENE_5); 	break;
			case 6:		frame_draw(IMG_HUXLEY_END_SCENE_6); 	break;
			case 7:		frame_draw(IMG_HUXLEY_END_SCENE_7); 	break;
			case 8:		frame_draw(IMG_HUXLEY_END_SCENE_8); 	break;
			case 9:		frame_draw(IMG_HUXLEY_END_SCENE_9); 	break;
			case 10:	frame_draw(IMG_HUXLEY_END_SCENE_10); 	break;
			case 11:	frame_draw(IMG_HUXLEY_END_SCENE_11); 	break;
		}//end of switch
		dmd_overlay_outline ();
		dmd_show2 ();
		task_sleep (TIME_500MS);
	}//END OF ENDLESS LOOP
}//end of mode_effect_deff



